import React from "react";

const styles = { select: { margin: "0 20px" } };

const getColumns = () => {
  return [
    {
      id: "checkbox",
      visible: true,
      pinned: true,
      width: "54px"
    },
    {
      id: "2",
      field: "username",
      label: "Username",

    },
    {
      id: "3",
      field: "first_name",
      label: "First Name"
    },
    {
      id: "4",
      field: "last_name",
      label: "Last Name"
    },
    {
      id: "5",
      field: "email",
      label: "Email"
    },
    {
      id: "6",
      field: "gender",
      label: "Gender",
      editorCellRenderer: ({
        tableManager,
        value,
        data,
        column,
        colIndex,
        rowIndex,
        onChange
      }) => (
        <select
          style={styles.select}
          value={value}
          onChange={(e) =>
            onChange({ ...data, [column.field]: e.target.value })
          }
        >
          <option>Male</option>
          <option>Female</option>
        </select>
      )
    },
    {
      id: "7",
      field: "ip_address",
      label: "IP Address"
    },
    {
      id: "8",
      field: "last_visited",
      label: "Last Visited",
      sort: ({ a, b, isAscending }) => {
        let aa = a.split("/").reverse().join(),
          bb = b.split("/").reverse().join();
        return aa < bb
          ? isAscending
            ? -1
            : 1
          : aa > bb
          ? isAscending
            ? 1
            : -1
          : 0;
      }
    }
  ];
};

export default getColumns;
