import React, {useState} from "react";
import GridTable from "@nadavshaar/react-grid-table";
import getColumns from "./getInventoryColumns";
import axios from 'axios';

export const Inventory = async (props) => { 
    /**
     * TODO move to .env
     */
    const baseUrl = "http://localhost:8000";

    console.log(props.productId);
    const access_token = sessionStorage.getItem('access_token');

    console.log(`access_token=${access_token}`);
    const data = await axios.post(`${baseUrl}/api/inventory`, {limit: 500, product_id: props.productId}, {headers: { Authorization: `Bearer ${access_token}` }})
    .catch(err => {console.log(err);})
    if(data) {
        props.stateChanger('products');
        props.setProducts(data?.data);
    }
    return (


        <div>
            <h2>Inventory</h2>
            <GridTable
                columns={getColumns()}
                rows={props.products}
                onRowClick={(
                    { data: { id }, isEdit },
                    { rowSelectionApi: { getIsRowSelectable, toggleRowSelection } }
                  ) => {props.setProductId(id);props.stateChanger('inventory');}
                }
            />
        </div>
      );
}
